$(function () {
    var e = 5;
    var v = 2;
    var a = 0;
    var u = 1;
    var b = 3;
    var d = 0;
    var m = 0;
    var p = "";
    var w = $(".plc");
    var g = $(".plc2");
    var r = $(".bread");
    var f = w.size() ? w : g;
    var l = f.size() ? f : r;
    var h = $(".menu");
    var n = h.children("li");
    var c = l.find("*");
    var k = c.size();
    var j = "active";
    h.find("li").removeClass(j);
    var t = new Object();
    t.getCurrentURL = function () {
        switch (MARK) {
            case "product":
                p = v ? v : MARK;
                break;
            case "news":
                p = e ? e : MARK;
                break;
            case "project":
                p = b ? b : MARK;
                break;
            case "agent":
                p = a ? a : MARK;
                break;
            case "help":
                p = u ? u : MARK;
                break;
            case "download":
                p = d ? d : MARK;
                break;
            case "job":
                p = m ? m : MARK;
                break;
            default:
                p = 0;
                break
        }
        return p
    };
    t.firstLiCur = function () {
        h.find("li:first").addClass(j)
    };
    t.selectedCur = function () {
        n.children("a").each(function () {
            var i = $(this).attr("href").toLowerCase();
            i = i.substring(i.lastIndexOf("/") + 1);
            if (i == p) {
                $(this).parent("li").addClass(j);
                return false
            } else {
                if (typeof (p) == "number") {
                    n.eq(p).addClass(j);
                    return false
                }
            }
        })
    };
    t.selectedTxtCur = function () {
        n.children("a").each(function () {
            var i = $.trim($(this).html()).replace(/<em>.*?<\/em>/gi, "");
            if (i == o && o.indexOf("首页") == -1) {
                $(this).parent("li").addClass(j);
                return false
            }
        });
        return h.children("li." + j)
    };
    t.hasCur = function () {
        var i = h.children("li." + j).size();
        return i
    };
    if (typeof (MARK) == "undefined") {
        t.firstLiCur();
        return false
    }
    if (typeof (XCurrent) != "undefined") {
        n.eq(XCurrent).addClass(j);
        return false;
    }
    if (!k) {
        p = t.getCurrentURL();
        t.selectedCur()
    }
    for (var q = k - 1; q >= 0; q--) {
        p = c.eq(q).attr("href");
        var o = $.trim(c.eq(q).text());
        if (p) {
            p = p.substring(p.lastIndexOf("/") + 1).toLowerCase();
            if (p == "" || p == undefined) {
                p = t.getCurrentURL()
            }
        }
        var s = t.selectedTxtCur();
        if (s.size() && o.indexOf("首页") == -1) {
            s.parent("li").addClass(j);
            return false
        } else {
            t.selectedCur();
            if (t.hasCur()) {
                return false
            }
        }
    }
    if (!t.hasCur()) {
        p = MARK;
        t.selectedCur();
        if (!t.hasCur()) {
            t.firstLiCur()
        }
    } else {
        t.firstLiCur()
    }
});
